use std::collections::HashSet;
fn main() {
    let input = "";
    println!("part 1: {}", find_nth_marker(4, Vec::from(input)));
    println!("part 2: {}", find_nth_marker(14, Vec::from(input)));
}

fn find_nth_marker(n: usize, input: Vec<u8>) -> usize {
    for (i, arr) in input.windows(n).enumerate() {
        let set: HashSet<&u8> = HashSet::from_iter(arr);
        if set.len() == n {
            return i + n;
        }
    }
    0
}